import React, { Component } from 'react';
// import { Link } from 'react-router-dom';
// import Edit from './edit.component';
import { confirmAlert } from 'react-confirm-alert'; 
import 'react-confirm-alert/src/react-confirm-alert.css'

import axios from 'axios';
class TableRow extends Component {

  constructor(props) {
    super(props);
    this.delete = this.delete.bind(this);
  }
  delete() {
      axios.delete('http://localhost:8080/list')
          .then(console.log('Deleted'))
          .catch(err => console.log(err))
  }

  CallBack = () =>{
    // e.preventDefault();
    // const obj = {
    //   id: this.props.obj.id,
    //   nom: this.state.nom,
    //   prenom: this.state.prenom
    // };
    // axios.put('http://localhost:8080/list', obj)
    //     .then(res => console.log(res.data));

    // this.props.history.push('/liste');
    console.log(this.newNom.value);

  }

  Edit = () => (
		confirmAlert({
		customUI: ({ onClose }) => {
			return (
				<div className='custom-ui edit'>
          <label>Nom</label>
          <input type="text" ref={(input)=>this.newNom = input } defaultValue={this.props.obj.nom}/><br/>
          <label>Pr√©nom</label>
          <input type="text" defaultValue={this.props.obj.prenom}/><br/>
					<button className={'boutton2'} onClick={this.CallBack()}>Ok</button>
					<button className={'boutton2'} onClick={onClose}>Annuler</button>
				</div>
			)
		}
	}))
  render() {
    return (
        <tr>
          <td>
            {this.props.obj.nom}
          </td>
          <td>
            {this.props.obj.prenom}
          </td>
          <td>
                {/* <Link to={"/edit/"+this.props.obj.id} className="btn btn-primary"><button>Edit</button></Link> */}
                <button onClick={this.Edit} className="btn btn-danger">Edit</button>
          </td>
          <td>
                <button onClick={this.delete} className="btn btn-danger">Delete</button>
          </td>
        </tr>
    );
  }
}

export default TableRow;
